package Projetos;

public class Problema1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int A = 1;
		int B = 9;
		
		while(A != B) {
			
			A++;
			B--;
		}
		
		System.out.println("O valor de A = " + A);
		System.out.println("O valor de B = " + B);
		
	}

}
